// Filter products by income/credit gates
const users = items[0].json.data || items[0].json;
const products = $items('Fetch Products')[0].json.data || $items('Fetch Products')[0].json;
const eligiblePairs = [];
for (const u of users) {
  for (const p of products) {
    if ((p.min_monthly_income ?? 0) <= (u.monthly_income ?? 0) && (p.min_credit_score ?? 0) <= (u.credit_score ?? 0)) {
      eligiblePairs.push({ user: u, product: p });
    }
  }
}
return eligiblePairs.map(pair => ({ json: pair }));
